mapper.select:
SELECT * , date_format(created_date, '%Y-%m-%d') created_show_date FROM ${tableName}
and subject like concat('%', #{search_text}, '%')
and contents like concat('%', #{search_text}, '%')
and ( subject like concat('%', #{search_text}, '%') or contents like concat('%', #{search_text}, '%') )
and id = #{id}
ORDER BY board_order desc LIMIT #{start}, #{end}

SELECT COUNT(*)
  FROM ${tableName}



            and subject like concat('%', #{search_text}, '%')


            and contents like concat('%', #{search_text}, '%')



        and id = #{id}

SELECT *
    ,   date_format(created_date, '%Y-%m-%d') created_show_date
  FROM ${tableName}
 WHERE NUM = #{boardNum}

SELECT
    (
        SELECT num
          FROM ${tableName}
         WHERE num IN(
                SELECT MAX(num)
                FROM ${tableName}
                WHERE num  <  #{boardNum}


                    and id = #{id}

        )) prevNum,

    (
        SELECT subject
          FROM ${tableName}
         WHERE num IN(
                SELECT MAX(num)
                FROM ${tableName}
                WHERE num  <  #{boardNum}


                    and id = #{id}

        )) prevSubject,

    (
        SELECT num
          FROM ${tableName}
         WHERE num IN(
                SELECT min(num)
                FROM ${tableName}
                WHERE num  >  #{boardNum}


                    and id = #{id}

        )) nextNum,

    (
        SELECT subject
          FROM ${tableName}
         WHERE num IN(
                SELECT min(num)
                FROM ${tableName}
                WHERE num  >  #{boardNum}


                    and id = #{id}

        )) nextSubject

