    	,	real_main_thumnail_img = #{real_main_thumnail_img}		
    			,	thumnail_path = #{thumnail_path}            			
    			    ,	main_view = #{main_view}            			        

 WHERE num = #{num}

UPDATE attach
   SET del_gb = 'y'
